import React, {useState} from 'react'

export default function CategoryReaction(props) {

    const [active, setActive] = useState(false);

  const handleChangeActive = () => {
    setActive((previousStar) => {
      return !previousStar;
    });
  };


  return (
    <div>
      <div className="toggle-wrapper" style={{display:"flex"}}>
        {active ? (<>
          <img
            className="active"
            src={props.Active}
            alt="yellow star"
            onMouseLeave={() => handleChangeActive()}
            height = "100%"
            width = "100%"
            style = {{opacity : "70%", position:"relative"}}
            ></img>
            <h2>
              NAKED
            </h2>
            </>
        ) : (
          <img
            className="inactive"
            src={props.Active}
            alt="black and white star"
            onMouseEnter={() => handleChangeActive()}
            height = "100%"
            width = "100%"

          />
        )}
      </div>
    </div>
  )
}
