import React, {useState} from "react";
import Inactive from "../../assets/icons8-heart-50.png";
import Active from "../../assets/icons8-heart.gif";



function ToggleLike() {

  const [active, setActive] = useState(false);

  const handleChangeActive = () => {
    setActive((previousStar) => {
      return !previousStar;
    });
  };


  return (
    <>
      <div className="toggle-wrapper">
        {active ? (
          <img
            className="active"
            src={Active}
            alt="yellow star"
            onClick={() => handleChangeActive()}
            height={30}
            width={30}
          />
        ) : (
          <img
            className="inactive"
            src={Inactive}
            alt="black and white star"
            onClick={() => handleChangeActive()}
            height={30}
            width={30}
          />
        )}
      </div>
    </>
  );
}

export default ToggleLike;
